function y = horner(c, x)
    % HORNER   Evaluate a polynomial using Horner's rule. 
    % Input:
    %   c     Coefficients of polynomial, in descending order (vector)
    %   x     Evaluation point (scalar)
    % Output:
    %   y     Value of the polynomial at x (scalar)

    n = length(c);
    y = c(1);
    for k = 2:n
        y = x * y + c(k);
    end
end

function myfun(x)
    s = sin(x)
    return exp(s)
end

function [y] = myfun(x)
    s = sin(x);
    y = exp(s);
end

c = 1;
mycfun = @(x) exp(c*sin(x));
mycfun(3)   % returns exp(1*sin(3))
c = 2;  
mycfun(3)   % also returns exp(1*sin(3))
mycfun = @(x) exp(c*sin(x));   % redefines mycfun
mycfun(3)   % now returns exp(2*sin(3))

def myfun(x):
    s = np.sin(x)
    return np.exp(s)
