f = @(x) sin( exp(2 * x) );
clf,  fplot(f, [0, 1], displayname="function")
xlabel('x'),  ylabel('f(x)')   
legend(location="southwest");

t = linspace(0, 1, 4)'; 
y = f(t);
p = polyinterp(t, y);
hold on,  fplot(p, [0, 1], displayname="interpolant on 4 nodes")
scatter(t, y, 'k', displayname="nodes")

