\begin{align*}
  \text{PDE:} \quad &  u_t = u_{xx}, \quad 0 < x < 1, \; t > 0, \\[1mm]
  \text{BC:} \quad &  u(0,t) = u(1,t) = 0,\quad t \ge 0, \\[1mm]
  \text{IC:} \quad &  u(x,0) = \sin (\pi x), \quad  0 \le x \le 1.
\end{align*}

\begin{align*}
  \frac{\partial \hat{u}}{\partial t} & = \left(- \pi^2 e^{- \pi^2 t} \right)\sin (\pi x), \\
  \frac{\partial^2 \hat{u}}{\partial x^2} & = e^{- \pi^2 t} \left[ - \pi^2 \sin (\pi x)\right].
\end{align*}

\begin{split}
  x_i &= ih, \quad h=\frac{S_\text{max}}{m}, \qquad i = 0,\ldots,m,  \\
  t_j &= j \tau, \quad \tau = \frac{T}{n}, \qquad j = 0,\ldots,n.
\end{split}

\frac{V_{i,j+1} - V_{i,j}}{\tau} = 
+ \frac{\sigma^2 x_i^2}{2} \frac{V_{i+1,j}-2V_{i,j}+V_{i-1,j}}{h^2}
+ r x_i \frac{V_{i+1,j}- V_{i-1,j}}{2h}  - rV_{i,j}.

m = 200;  h = Smax / m;
x = h*(0:m)';
n = 1000;  tau = T / n;
t = tau*(0:n)';
lambda = tau / h^2;  mu = tau / h;

V = zeros(m+1, n+1);
V(:,
1) = max(0, x - K);
for j = 1:n
    % Fictitious value from Neumann condition.
    Vfict = 2*h + V(m, j);
    Vj = [ V(:, j); Vfict ];
    % First row is zero by the Dirichlet condition.
    for i = 2:m+1 
        diff1 = (Vj(i+1) - Vj(i-1));
        diff2 = (Vj(i+1) - 2*Vj(i) + Vj(i-1));
        V(i,j+1) = Vj(i) ...
            + (lambda * sigma^2* x(i)^2/2) * diff2  ...
            + (r*mu * x(i))/2 * diff1 - r*tau * Vj(i);
    end 
end

Smax = 8;  
K = 3;  sigma = 0.06;  r = 0.08;
m = 200;  h = Smax / m;
x = h*(0:m)';
T = 8;
n = 1000;  tau = T / n;
t = tau*(0:n)';
lambda = tau / h^2;  mu = tau / h;
V = zeros(m+1, n+1);
V(:, 1) = max(0, x - K);
for j = 1:n
    % Fictitious value from Neumann condition.
    Vfict = 2*h + V(m, j);
    Vj = [ V(:, j); Vfict ];
    % First row is zero by the Dirichlet condition.
    for i = 2:m+1 
        diff1 = (Vj(i+1) - Vj(i-1));
        diff2 = (Vj(i+1) - 2*Vj(i) + Vj(i-1));
        V(i, j+1) = Vj(i) ...
             + (lambda*sigma^2 * x(i)^2/2) * diff2  ...
             + (r*mu * x(i))/2 * diff1 - r*tau * Vj(i);
    end   
end

index_times = 1 + 250*(0:4);
show_times = t(index_times);
for j = index_times
    str = sprintf("t = %.2f", t(j));
    plot(x, V(:, j), displayname=str) 
    hold on
end
title('Black-Scholes instability')
xlabel('stock price'),  ylabel('option value')
axis tight,  grid on
legend(location="northwest")

