ivp.RelativeTolerance = 1e-13;
u_exact = solutionFcn(ivp, a, b);

n = round(5 * 10.^(0:0.5:3));
err = [];
for k = 1:length(n)
    [t, u] = eulerivp(ivp, a, b, n(k));
    err(k) = norm(u_exact(t) - u, Inf);
end
