m = 100;  
[x, Dx, Dxx] = diffper(m, [0, 1]);
Ix = eye(m);

tfinal = 0.15;  n = 2400;  
tau = tfinal / n;  t = tau * (0:n)';
U = zeros(m, n+1);
U(:, 1) = exp( -60*(x - 0.5).^2 );

A = sparse(Ix + tau * Dxx);
for j = 1:n
    U(:, j+1) = A * U(:,j);
end

index_times = 1:10:31;
show_times = t(index_times);
clf
for j = index_times
    str = sprintf("t = %.2e", t(j));
    plot(x, U(:, j), displayname=str) 
    hold on
end
legend(location="northwest")
xlabel('x'), ylabel('u(x,t)')
title('Heat equation by forward Euler')

clf
index_times = 1:101;
plot(x, U(:, 1))
hold on,  grid on
axis([0, 1, -1, 2])
title('Heat equation by forward Euler') 
xlabel('x'),  ylabel('u(x,t)')
vid = VideoWriter("figures/diffusionFE.mp4", "MPEG-4");
vid.Quality = 85;
open(vid);
for frame = index_times
    cla, plot(x, U(:, frame))
    str = sprintf("t = %.3f", t(frame));
    text(0.05, 0.92, str);
    writeVideo(vid, frame2im(getframe(gcf)));
end
close(vid)

B = sparse(Ix - tau * Dxx);
[l, u] = lu(B);
for j = 1:n
    U(:, j+1) = u \ (l \ U(:, j));
end

