def hatfun(x, t, k):
    """
    hatfun(x,t,k)

    Evaluate a piecewise linear "hat" function at `x`, where `t` is a vector of
    n+1 interpolation nodes and `k` is an integer in 0:n giving the index of the node
    where the hat function equals one.
    """
    n = len(t) - 1

    # Return correct node given mathematical index k, including fictitious choices.
    def node(k):
        if k < 0:
            return 2 * t[0] - t[1]
        elif k > n:
            return 2 * t[n] - t[n - 1]
        else:
            return t[k]

    H1 = (x - node(k - 1)) / (node(k) - node(k - 1))  # upward slope
    H2 = (node(k + 1) - x) / (node(k + 1) - node(k))  # downward slope
    H = np.minimum(H1, H2)
    return np.maximum(0, H)

function p = plinterp(t,y)
% PLINTERP   Piecewise linear interpolation.
% Input:
%   t     interpolation nodes (vector, length n+1)
%   y     interpolation values (vector, length n+1)
% Output:
%   p     piecewise linear interpolant (function)

n = length(t)-1;
p = @evaluate;

    % This function evaluates p when called.
    function f = evaluate(x)
        f = 0;
        for k = 0:n
            f = f + y(k+1)*hatfun(x,t,k);
        end
    end

end

f = @(x) exp(sin(7 * x));
x = linspace(0, 1, 10001)';    % sample the difference at many points
n = round(10.^(1:0.25:3.5))';
maxerr = zeros(size(n));
for i = 1:length(n)
    t = (0:n(i)) / n(i);       % interpolation nodes
    p = plinterp(t, f(t));
    maxerr(i) = norm(f(x) - p(x), Inf);
end
disp(table(n(1:4:end), maxerr(1:4:end), variableNames=["n", "inf-norm error"]))
