x = linspace(-1, 1, 1601)';
Phi = zeros(size(x));
for n = 10:10:50
    theta = linspace(0, pi, n+1)';
    t = -cos(theta);                    
    for k = 1:length(x)
        Phi(k) = prod(x(k) - t);
    end
    semilogy(x, abs(Phi));  hold on
end
axis tight, title('Effect of Chebyshev nodes')    
xlabel('x'), ylabel('|\Phi(x)|')   
ylim([1e-18, 1e-2])

