[x, Dx, Dxx] = diffper(300, [-4, 4]);
f = @(t, u, c) -c * (Dx*u);
ivp = ode(ODEFcn=f);
ivp.Parameters = 2;
ivp.InitialTime = 0;
ivp.RelativeTolerance = 1e-5;

u_init = 1 + exp(-3*x.^2);
ivp.InitialValue = u_init;
t = linspace(0, 3, 201);
sol = solve(ivp, t);
U = sol.Solution;

clf
plot(x, U(:, 1))
hold on,  grid on
axis([-4, 4, 0.9, 2.1])
title('Advection equation with periodic boundary') 
xlabel('x'),  ylabel('u(x,t)')
vid = VideoWriter("advection-periodic.mp4","MPEG-4");
vid.Quality = 85;
open(vid);
for frame = 1:length(t)
    cla, plot(x, U(:, frame))
    str = sprintf("t = %.2f", t(frame));
    text(-3.5, 1.9, str);
    writeVideo(vid, frame2im(getframe(gcf)));
end
close(vid)
close(gcf)

for t in arange(0, 3, 2/3):
    plot(x, sol.sol(t), label=f"t = {t:.1f}")
legend()
xlabel("$x$"),  ylabel("$u(x,t)$")
title("Advection with periodic boundary");

